func GetZyberPoolInfo(client *ethclient.Client) (*big.Int, *big.Int, error) {
	zyberPool := common.HexToAddress(zyberPoolAddress)

	poolABI, err := abi.JSON(strings.NewReader(zyberPoolABI))
	if err != nil {
		log.Fatalf("Failed to parse Zyber pool ABI: %v", err)
	}

	reservesFunction := poolABI.Methods["getReserves"]

	callMsg := ethereum.CallMsg{
		To:   &zyberPool,
		Data: reservesFunction.ID,
	}

	out, err := client.CallContract(context.Background(), callMsg, nil)
	if err != nil {
		log.Fatalf("Failed to call Zyber pool contract: %v", err)
	}

	reserves, err := reservesFunction.Outputs.Unpack(nil, out)
	if err != nil {
		log.Fatalf("Failed to unpack Zyber pool reserves: %v", err)
	}

	reserve0, _ := reserves[0].(*big.Int)
	reserve1, _ := reserves[1].(*big.Int)

	return reserve0, reserve1, nil
}

func ZyberPrice(client *ethclient.Client) (*big.Float, error) {
	reserve0, reserve1, err := GetZyberPoolInfo(client)
	if err != nil {
		log.Fatalf("Failed to get Zyber pool info: %v", err)
	}

	price := new(big.Float).Quo(new(big.Float).SetInt(reserve0), new(big.Float).SetInt(reserve1))
	return price, nil
}
